<?php

namespace Tests\Feature\app\Http\Controllers;

use App\Http\Controllers\ImageController;
use Tests\TestCase;

class ImageControllerTest extends TestCase
{

    private static $phpSampleImage = "";
    private static $phpInvalidSampleImage = "SGFoYWhhDQoJSG9ob2hvDQoJCUhpaGloaQ0KCQkJSG91aG91aG91";

    public function testSaveValidImage()
    {
        $imageController = new ImageController();
        self::assertEquals(
            'test.jpg',
            $imageController->save(
                'test',
                self::$phpSampleImage,
                public_path() . '/images/'
            )
        );
    }

    public function testSaveInvalidImage()
    {
        $imageController = new ImageController();
        self::assertEquals(
            'Invalid data',
            $imageController->save(
                'test',
                self::$phpInvalidSampleImage,
                public_path() . '/images/'
            )
        );
    }

    public function testDeleteExistImage()
    {
        $imageController = new ImageController();
        self::assertEquals(
            'Deleted',
            $imageController->delete(
                'test',
                public_path() . '/images/'
            )
        );
    }

    public function testDeleteNotExistImage()
    {
        $imageController = new ImageController();
        self::assertEquals(
            'File not found',
            $imageController->delete(
                'test',
                public_path() . '/images/'
            )
        );
    }
}
